package Negocio.tomaynotificacionpedidos;

public class Producto {

	public String nombre;
	public int valor;
	
	public Producto(String nombre, int valor) {
		this.nombre = nombre;
		this.valor = valor;
	}
	
	public String getNombre() {
		// TODO Auto-generated method stub
		return this.nombre;
	}

	public int getValor() {
		// TODO Auto-generated method stub
		return this.valor;
	}

}
